defmodule RosalindDnaTest do
  use ExUnit.Case

  test "Rosalind.Dna.process test with dataset0" do
    IO.puts Rosalind.Dna.description
    data = "AGCTTTTCATTCTGACTGCAACGGGCAATATGTCTCTGTGTGGATTAAAAAAAGAGTGTCTGATAGCAGC"
    assert Rosalind.Dna.process(data) == "20 12 17 21"
  end

  test "Rosalind Dna.process test with dataset1" do
    data = ""
    assert Rosalind.Dna.process(data) == "223 227 226 239"
  end

  test "Rosalind Dna.process test with dataset2" do
    data = ""
    assert Rosalind.Dna.process(data) == "236 227 236 186"
  end
end
